import { useNavigate } from "react-router-dom";
import { useAuthContext } from "./AuthContext"; // Import your AuthContext

const useNavigationHelpers = () => {
  const navigate = useNavigate();
  const { dispatch } = useAuthContext(); // Access the dispatch method from AuthContext

  const goToMyListings = () => {
    navigate("/mylistings");
  };

  const goToNewListing = () => {
    navigate("/newlisting");
  };

  const logout = () => {
    // Clear local storage
    localStorage.removeItem("authState");

    // Clear global auth state and local storage
    clearAuthState();

    // Redirect to login page
    navigate("/login");

    console.log("User logged out and global state cleared");
  };

  return { goToMyListings, goToNewListing, logout };
};

export default useNavigationHelpers;
