import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../Signup.css'; // Optional: Custom styles for the component
import { useNavigate } from 'react-router-dom';

const NewListing = () => {
  const navigate = useNavigate();
  const [itemTitle, setItemTitle] = useState('');
  const [description, setDescription] = useState('');
  const [minBidValue, setMinBidValue] = useState('');
  const [file, setFile] = useState(null);

  const handleItemTitleChange = (e) => {
    setItemTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleMinBidValueChange = (e) => {
    setMinBidValue(e.target.value);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const redirectToMyListings = () => {
    navigate('/mylistings');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Implement your logic here (e.g., API call)
    console.log('Item Title:', itemTitle);
    console.log('Description:', description);
    console.log('Minimum Bid Value:', minBidValue);
    console.log('Uploaded File:', file);
    redirectToMyListings();
  };

  return (
    <div className="signup-container container d-flex justify-content-center align-items-center vh-100">
      <div className="signup-form">
        <h2 className="text-center mb-4">New Listing</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group row mb-3">
            <label htmlFor="itemTitle" className="col-sm-4 col-form-label text-sm-right">
              Item Title
            </label>
            <div className="col-sm-8">
              <input
                type="text"
                className="form-control"
                id="itemTitle"
                value={itemTitle}
                onChange={handleItemTitleChange}
                placeholder="Enter item title"
                required
              />
            </div>
          </div>
          <div className="form-group row mb-3">
            <label htmlFor="description" className="col-sm-4 col-form-label text-sm-right">
              Description
            </label>
            <div className="col-sm-8">
              <textarea
                className="form-control"
                id="description"
                value={description}
                onChange={handleDescriptionChange}
                placeholder="Enter description"
                rows="3"
                required
              />
            </div>
          </div>
          <div className="form-group row mb-3">
            <label htmlFor="minBidValue" className="col-sm-4 col-form-label text-sm-right">
              Minimum Bid Value
            </label>
            <div className="col-sm-8">
              <input
                type="number"
                className="form-control"
                id="minBidValue"
                value={minBidValue}
                onChange={handleMinBidValueChange}
                placeholder="Enter minimum bid value"
                required
              />
            </div>
          </div>
          <div className="form-group row mb-3">
            <label htmlFor="fileUpload" className="col-sm-4 col-form-label text-sm-right">
              Upload File
            </label>
            <div className="col-sm-8">
              <input
                type="file"
                className="form-control-file"
                id="fileUpload"
                onChange={handleFileChange}
                required
              />
            </div>
          </div>
          <div className="d-flex justify-content-end mt-3">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default NewListing;
